sourcesContent:
import mongoose from "mongoose";

const userSchema = new mongoose.Schema({
    name: {
        type: String,
        required: [true, "Tên người dùng là bắt buộc"],
        minlength: [3, "Tên người dùng cần tối thiểu 3 ký tự"]
    },
    email: {
        type: String,
        required: [true, "Email là bắt buộc"],
        unique: true,
        match: [/^\S+@\S+\.\S+$/, "Email không hợp lệ"]
    },
    password: {
        type: String,
        required: [true, "Mật khẩu là bắt buộc"],
        minlength: [6, "Mật khẩu cần tối thiểu 6 ký tự"],
        select: false // không trả về mật khẩu khi truy vấn
    },
    role: {
        type: String,
        enum: ['customer', 'staff', 'admin'],
        default: 'customer'
    },
    phone:{
        type: String,
        required: [true, "Số điện thoại là bắt buộc"],
        match: [/^\d{10,11}$/, "Số điện thoại không hợp lệ"]

    },
    addresses: [
    {
      street: String,
      city: String,
      isDefault: {
        type: Boolean,
        default: false,
      },
    },
  ],
    active: {
        type: Boolean,
        default: true
    }
}, {
         timeStamp:true,
    versionKey: false // loại bỏ version của bản ghi (__v:1)
})

const User = mongoose.model("User", userSchema);

export default User;

