import mongoose from "mongoose";
import paginate from 'mongoose-paginate-v2'

const foodSchema = new mongoose.Schema(
  {
    name: {
      type: String,
      required: true,
      min: 5,
    },
    description: {
      type: String,
      required: true,
    },
    price: {
      type: Number,
      required: true,
      min: 0,
    },
    isVailable: {
      type: Boolean,
      required: true,
    },
    category: {
      type: String,
      required: true,
    },
  },
  {
    timestamps: true,
    versionKey: false,
  }
);
 foodSchema.plugin(paginate)
const Food = mongoose.model("Food", foodSchema);

export default Food;

