sourcesContent:
import mongoose from "mongoose";
import paginate  from "mongoose-paginate-v2";
const taskSchema = new mongoose.Schema({
    title: {
        type: String,
        required: true,
        min: 5
    },
     description: {
        type: String,
        required: true,
    },

     status: {
        type: Number,
        required: true,
        default:1
    },

     category: {
        type: String,
        required: true,
    },
},{
    versionKey:false,
    timestamps:true
})
    taskSchema.plugin(paginate)
    const Task = mongoose.model('task', taskSchema)
    export default Task

