sourcesContent:
import express from 'express';
import {
  getAllProduct,
  getById,
  addProduct,
  updateProduct,
  deleteProduct
} from '../controllers/product.controller'// destructuring
// import { createPosts, DeletePosts, getAllPosts, getPostsById, UpdatePosts } from '../controllers/posts.controller';
import { createProductSchema } from '../validations/product.validations';
import { validateRequest } from '../middlewares/validateRequest';
import { checkAuth, checkRole } from '../middlewares/checkAuth';
const router = express.Router();

router.use(checkAuth) // Kiểm tra xác thực người dùng trước khi truy cập các route bên dưới

// Lấy danh sách sản phẩm
router.get('/products', getAllProduct)

// Lấy thông tin chi tiết sản phẩm
router.get('/products/:id', getById)

// Thêm mới 1 sản phẩm
router.use(checkRole("staff", "admin"))

router.post('/products',validateRequest(createProductSchema) , addProduct)

// Cập nhật sản phẩm
router.put('/products/:id', validateRequest(createProductSchema),updateProduct)

// Xóa sản phẩm
router.delete('/products/:id',deleteProduct)

//Posts
// router.get('/posts', getAllPosts)
// router.get('/posts/:id', getPostsById)
// router.post('/posts', createPosts)
// router.put('/posts/:id', UpdatePosts)
// router.delete("/posts/:id", DeletePosts)
export default router;

