sourcesContent:
import Post from "../models/posts.model"
 export const getAllPosts = async (req, res) => {
  try {
    const posts = await Post.find();
    return res.status(200).json({ message: 'Lấy dữ liệu thành công', data: posts });
  } catch (error) {
    return res.status(400).json({ message: 'Lỗi khi lấy dữ liệu', error: error.message });
  }
};
  export const getPostsById = async (req, res) => {
    try{
      const post = await Post.findById(req.params.id);
      if(!post){
        return res.status(400).json({massage: "khong tim thay "});
      }
      return res.status(200).json({massage: "lay du lieu thanh cong", data: post});

    }catch(error){
      return res.status(400).json({massage: error.message})
    }
  }
  export const createPosts = async (req, res) => {
  try{
    const newPost = await Post.create(req.body);
    return res.status(200).json({massage: "them du lieu thanh cong", data: newPost});
  }catch(error){
      return res.status(400).json({massage: error.message})
  }
}
  export const UpdatePosts = async (req, res) => {
     try {
    const updatePost = await Post.findByIdAndUpdate(req.params.id, req.body,{
      new: true,
      runValidators: true,
    })

    if(!updatePost){
      return res.status(400).json({message: "Cập nhật sản phẩm thất bại"})
    }

    return res.status(200).json({
      message: "Cập nhật thành công",
      data: updatePost
    })

  } catch (error) {
    return res.status(400).json({message: error.message})
  }
  }
  export const DeletePosts = async (req,res) =>{
   try {
    const post = await Post.findByIdAndDelete(req.params.id);
    
    if(!post){
      return res.status(404).json({message: "Không tìm thấy "})
    }

    return res.status(200).json({
      message: "Xóa thành công"
    })
  } catch (error) {
    return res.status(400).json({message: error.message})
  }
  }

