sourcesContent:
import jwt from "jsonwebtoken";
import User from "../models/user.model";


export const checkAuth = (req, res, next) => {
    const header = req.headers["authorization"];
    if (!header) {
        return res.status(401).json({ message: "thiếu header" });
    }
    const token = header.split(" ")[1];
    if (!token) {
        return res.status(401).json({ message: "thiếu token" });
    }
     jwt.verify(token,"123456",(err,decode) => {
    // console.log(decode);
    if(err){
      return res.status(403).json({message: "Sai token hoặc hết hạn"})
    }
    req.userId = decode.id;
  })
    next();
  }
    export const checkRole =  (...roles) => {
      return async (req, res, next) => {
        try {
          const user = await User.findById(req.userId);
          if(!user){
            return res.status(403).json({message: "Người dùng không tồn tại"});
          }
          const isRole = roles.includes(user.role);
          if(!isRole){
            return res.status(403).json({message: "bạn không có quyền truy cập"});
            next();
          }
        } catch (error) {
          return res.status(500).json({message: error.message})
        }
      }
    }

