import Food from "../models/food.model"

export const getAllFood = async(req, res) => {
    try {
        const foods = await Food.find();
        return res.status(200).json({message:"lay du lieu thanh cong"})
    } catch (error) {
     return   res.status(500).json({message: message.error})
    }
}
export const getById = async (req, res) => {
    
    try{
        const food = await Food.findById(res.params.id)
        if(!food){
            return  res.status(500).json({message: "khong tim thay id"})
        }
        return res.status(200).json({message:"lay du lieu thanh cong", data: food})
    }catch (error) {
     return   res.status(500).json({message: message.error})
    }
}
export const createFood = async(req, res) => {
    try{
        const newfood = await Food.create(req.body);
return res.status(200).json({message:"them du lieu thanh cong", data: newfood})
    }catch (error) {
     return   res.status(500).json({message: error.message})
     
    }
}
export const updateFood = async(req, res) => {
    try {
        const update = await Food.findByIdAndUpdate(req.params.id, req.body,{
            new: true,
            runValidators: true,
        })
        if(!update){
            return  res.status(500).json({message: "cap nhat san pham that bai"})
        }
        return res.status(200).json({message:"sua du lieu thanh cong", data: update})
    } catch (error) {
        return   res.status(500).json({message: message.error})
    }
   
}
