sourcesContent:
 import mongoose from "mongoose";

 const userSchema = new mongoose.Schema({
    username:{
        type: String,
        required:true,
       
    },
     email:{
         type: String,
        required:true,
        unique: true
    },
     password :{
        type: String,
        required:true
        
    },
     role :{
             type: String,
             role: ["user", "admin"],
             default: "user"
    }
}, {
    timestamps: true,
    versionKey: false
 })
 const User = mongoose.model("user", userSchema);
 export default User

