sourcesContent:
import { data } from "react-router";
import Task from "../models/task.model";

export const getAll = async(req, res) => {
        try {
            const{
                limit = 5,
                page = 1,
                status = 1,
                sortOrder = 'desc'
            } = req.query
            const options = {
                limit,
                page,
                status,
                sort: {'createdAd': sortOrder == 'desc' ? 1 : -1}
            }
            const tasks = await Task.paginate({}, options)
            if(!tasks){
                return res.status(500).json({message: "thatbai"})
            }
            return res.status(200).json({message: "lay data thanh cong", data: tasks})
        } catch (error) {
            return res.status(500).json({message: error.message})
        }
}
export const getById = async(req, res) => {
        try {
            const id = await Task.findById(req.params.id)
             if(!id){
                return res.status(500).json({message: "khong tim thay"})
            }
            
               return res.status(200).json({message: "lay data thanh cong", data: id})
            
        } catch (error) {
            return res.status(500).json({message: error.message})
        }
}
export const add = async(req, res) => {
        try {
            const addNew = await Task.create(req.body)
            if(!addNew){
                 return res.status(500).json({message: "them that bai"})
            }
             return res.status(200).json({message: "them thanh cong"})
        } catch (error) {
            return res.status(500).json({message: error.message})
        }
}
export const edit = async(req, res) => {
        try {
            const updateNew = await Task.findByIdAndUpdate(req.params.id, req.body, {
                new:true,
                data: updateNew
            })
              if(!updateNew){
                 return res.status(500).json({message: "sua that bai"})
            }
             return res.status(200).json({message: "sua thanh cong", data: updateNew})
        } catch (error) {
            return res.status(500).json({message: error.message})
        }
}
export const xoa = async(req, res) => {
        try {
            const deleteTask = await Task.findByIdAndDelete(req.params.id)
            if(!deleteTask){
                return res.status(500).json({message: "xoa that bai"})
            }
            return res.status(200).json({message: "xoa thanh cong"})
        } catch (error) {
            return res.status(500).json({message: error.message})
        }
}

