sourcesContent:
import User from "../models/user.model.js";
import bcrypt from "bcryptjs";

export const register = async (req, res) => {
    try {
        const { username, email, password } = req.body;
        const hashedPassword = await bcrypt.hash(password, 10);
        const user = new User({ username, email, password: hashedPassword });
        if(!user){
            return  res.status(500).json({ message:"dang ky that bai" });
        }
        await user.save();
        return res.status(201).json({ message: "dang ky thanh cong", user: { username, email } });
    } catch (error) {
       return  res.status(500).json({ message:error.message });
    }
};
export const login = async(req, res) => {
    try {
        const {email, password} = req.body
        const ImLogin = await User.findOne({email}, {password})
        if(!ImLogin){
            return res.status(500).json({message: "dang nhap that bai"})
        }
         return res.status(201).json({ message: "dang nhap thanh cong"});
    } catch (error) {
        return res.status(500).json({message: error.message})
    }
}

